import React, {useEffect, useState} from 'react';
import {Link} from "@reach/router";
import { makeStyles } from '@material-ui/core/styles';
import axios from "axios";
import Button from "@material-ui/core/Button";
import clsx from 'clsx';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Grid from "@material-ui/core/Grid";
import color from "../images/colorJuice.png";

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
    },
    div: {
        display: 'inline-block',
        margin: '3em',
    },
    card: {
        width: 345,
        height: 450,
    },
    media: {
        height: 0,
        paddingTop: '60%'
    },
    description: {
        margin: '1em 1.5em 0'
    },
    cart: {

    }
}));

const JuiceBody = ({ cart, setCart, juices, setJuices, total, setTotal, selected, setSelected}) => {

    const classes = useStyles();

    const addToCart = (juice) => {
        setCart([...cart, juice])
        setTotal((total + juice.price) * 1.0625)
    }
    const addId = (juice) => {
        setSelected(juice._id)
    }

    const listJuices = juices.map((juice) => (
        <div className={classes.div}>
            <Card className={classes.card}>
                <Link to={"/juices/" + juice._id}  onClick={() => addId(juice)}>
                    <CardHeader
                        title={juice.title}
                        subheader={juice.type}
                    />
                    <CardMedia
                        className={classes.media}
                        image={juice.url}
                    />
                    <h5 className={classes.description}>{juice.description}</h5>
                </Link>
                <div>
                    <Button type="submit" onClick={() => addToCart(juice)} className={classes.cart}>Add to cart </Button>
                </div>
            </Card>
        </div>
    ));

    return (
        <div className={classes.root}>
            <div> {listJuices} </div>
        </div>
    )
}
export default JuiceBody;
