import React, {useEffect, useState} from 'react';
import {Link} from "@reach/router";
import { makeStyles } from '@material-ui/core/styles';
import axios from "axios";
import Button from "@material-ui/core/Button";
import clsx from 'clsx';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Grid from "@material-ui/core/Grid";
import color from "../images/colorJuice.png";

const useStyles = makeStyles((theme) => ({
    div: {
        display: 'inline-block',
        margin: '3em',
    },
    card: {
        width: 345,
        height: 400,
    },
    media: {
        height: 0,
        paddingTop: '60%'
    },
}));

const JuiceBody = ({ cart, setCart, juices, setJuices, total, setTotal, selected, setSelected}) => {

    const classes = useStyles();

    const addToCart = (juice) => {
        setCart([...cart, juice])
        setTotal((total + juice.price) * 1.0625)
    }
    const addId = (juice) => {
        setSelected(juice._id)
    }

    const listJuices = juices.map((juice) => (
        <div className={classes.div}>
            <Card className={classes.card}>
                <Link to={"/juices/" + juice._id}  onClick={() => addId(juice)}>
                    <CardHeader
                        title={juice.title}
                        subheader={juice.type}

                    />
                    <CardMedia
                        className={classes.media}
                        image={juice.url}
                    />
                    <h5>{juice.description}</h5>
                </Link>
                <Button type="submit" onClick={() => addToCart(juice)}>Add to cart </Button>
            </Card>
        </div>
    ));

    return (
        <div>
            <div> {listJuices} </div>
        </div>
    )
}
export default JuiceBody;
