import React, {useReducer, useEffect, useState} from 'react';
import axios from "axios";
import { makeStyles } from '@material-ui/core/styles';
import Paper from "@material-ui/core/Paper";
import Card from "@material-ui/core/Card";

const useStyles = makeStyles((theme) => ({
    paper: {
        width: '60vw',
        height: '30em',
        marginLeft: '2em',
        textAlign: 'left',
        display: 'inline-block'
    },
    img: {
        display: 'inline-block',
        height: '80%',
        marginTop: '-5em'
    },
    juiceTitle: {
        fontFamily: "BebasNeue, serif",
        fontSize: '5em'
    },
    text: {
        display: 'inline-block',
        fontFamily: 'Montserrat, serif',
        marginLeft: '2em',
        marginTop: '3em'
    },
    header: {
        fontFamily: 'BebasNeue, serif',
        fontSize: '10em'
    },
    body: {
        fontSize: '2em',
    },
    cart: {
        display: 'inline-block',
        width: '20%',
    },
    box: {
        display: 'flex',
        justifyContent: 'space-around'
    }
}));

const Cart = ({ cart, setCart, juices, setJuices, total, setTotal }) => {
    const classes = useStyles();

    let sum = 0;
    for(let i = 0; i < total.length; i++) {
        sum += total[i]
    }
    sum = sum.toFixed(2)

    const removeFromCart = (juice) => {
        let hardCopy = [...cart];
        hardCopy = hardCopy.filter((cartItem) => cartItem._id !== juice._id);
        setCart(hardCopy);
        let totalCopy = [...total];
        totalCopy = totalCopy.filter((totalItem) => totalItem._id !== total._id)
        setTotal(totalCopy)
        for(let i = 0; i < total.length; i++) {
            sum += total[i]
        }
    };

    const listCart = cart.map((juice) => (
        <Paper elevation={5} className={classes.paper}>
            <img className={classes.img} src={juice.url}/>
            <div className={classes.text}>
                <h1 className={classes.juiceTitle}>{juice.title}</h1>
                <p className={classes.body}>Price: {juice.price}</p>
                <p className={classes.body}>{juice.description}</p>
                <input type="submit" value="Remove" onClick={() => removeFromCart(juice)}/>
            </div>
        </Paper>
    ));

    return (
        <div>
            <h1 className={classes.header}>Cart</h1>
            <div className={classes.box}>
                <div>{listCart}</div>
                <Card className={classes.cart}>
                    <h2>Cart Items: {cart.length}</h2>
                    <h2>Total: {sum}</h2>
                </Card>
            </div>
        </div>
    );
};

export default Cart;
