import React, { useEffect, useState } from 'react';
import {Link, navigate} from '@reach/router';
import axios from 'axios';
import Button from "@material-ui/core/Button";

const SingleJuiceBody = (props) => {
    const [juice, setJuice] = useState({})
    const [cart, setCart] = useState([])

    const onClickHandler = (e) => {
        e.preventDefault();
        props.onAddCart( cart );
    }

    useEffect(() => {
        axios.get("http://localhost:8000/api/juices/" + props.id)
            .then((res) => {
                console.log(res.data);
                setJuice(res.data);
            })
            .catch((err) => console.log(err))
    }, [])

    return (
        <div className="container">
            <p className="text-center h1"><span className="text-muted">Title:</span> {juice.title}</p>
            <p className="text-center h1"><span className="text-muted">Price:</span> {juice.price}</p>
            <p className="text-center h1"><span className="text-muted">Description:</span> {juice.description}</p>
            <p className="text-center h1"><span className="text-muted">Type:</span> {juice.type}</p>
            <Button type="submit" value="Add To Cart" onClick={onClickHandler}>Add to cart</Button>
            <Link to={"/juices"}>
                <Button>Go Back</Button>
            </Link>
        </div>
    )
}
export default SingleJuiceBody;
