import React, {useEffect, useState} from 'react';
import axios from "axios";



const Cart = ({ cart, setCart, juices, setJuices }) => {
    const removeFromCart = (juice) => {
        let hardCopy = [...cart];
        hardCopy = hardCopy.filter((cartItem) => cartItem.id !== juice._id);
        setCart(hardCopy);
    };
    const listCart = cart.map((juice) => (
        <div>
            <h1>{juice.title}</h1>
            <input type="submit" value="remove" onClick={() => removeFromCart(juice)} />
        </div>
    ));

    return (
        <div>
            <h1>Current Message</h1>
            <div>{listCart}</div>
        </div>
    );
};

export default Cart;
