import React, {useReducer, useEffect, useState} from 'react';
import axios from "axios";

const Cart = ({ cart, setCart, juices, setJuices }) => {

    const [total, setTotal] = useState(0)

    const removeFromCart = (juice) => {
        let hardCopy = [...cart];
        hardCopy = hardCopy.filter((cartItem) => cartItem._id !== juice._id);
        setCart(hardCopy);
    };

    const getTotal = (total) => {
        return total.toLocaleString(undefined, currencyOptions)
    }

    const listCart = cart.map((juice) => (
        <div>
            <h1>{juice.title}</h1>
            <h1>{juice.price}</h1>
            <input type="submit" value="remove" onClick={() => removeFromCart(juice)} />
        </div>
    ));

    return (
        <div>
            <h1>Cart</h1>
            <h2>Cart Items: {cart.length}</h2>
            <div>{listCart}</div>
            <h2>Total: {total}</h2>
        </div>
    );
};

export default Cart;
