import React, { useEffect, useState } from 'react';
import {Link, navigate} from '@reach/router';
import axios from 'axios';
import Button from "@material-ui/core/Button";
import { makeStyles } from '@material-ui/core/styles';
import Paper from "@material-ui/core/Paper";

const useStyles = makeStyles((theme) => ({
    btn : {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        width: 'auto',
        height: 'auto',
        marginTop: '1em',
        alignContent: 'center'
    },
    cart: {
        background: 'linear-gradient(to right, rgb(182, 244, 146), rgb(51, 139, 147))',
        borderRadius: 30,
        border: 0,
        color: 'white',
        height: 48,
        padding: '0 30px',
        boxShadow: '0 3px 5px 2px rgba(51,139,147,.3)',
        bottom: 0,
    },
    back: {
        background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
        borderRadius: 30,
        border: 0,
        color: 'white',
        height: 48,
        padding: '0 30px',
        boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
        bottom: 0,
    },
    paper: {
        width: '60vw',
        alignItems: 'center'
    }
}));

const SingleJuiceBody = ({ cart, setCart, total, setTotal, selected, setSelected, juice, setJuice}) => {

    const classes = useStyles();

    const onClickHandler = (e) => {
        e.preventDefault();
    }

    useEffect(() => {
        axios.get("http://localhost:8000/api/juices/" + selected)
            .then((res) => {
                console.log(res.data);
                setJuice(res.data);
            })
            .catch((err) => console.log(err))
    }, [])

    const addToCart = (juice) => {
        setCart([...cart, juice])
        setTotal((total + juice.price) * 1.0625)
    }

    return (
        <div>
            <Paper elevation={3} className={classes.paper}>
            <p className="text-center h1"><span className="text-muted">Title:</span> {juice.title}</p>
            <p className="text-center h1"><span className="text-muted">Price:</span> {juice.price}</p>
            <p className="text-center h1"><span className="text-muted">Description:</span> {juice.description}</p>
            <p className="text-center h1"><span className="text-muted">Type:</span> {juice.type}</p>
            <Button type="submit" value="Add To Cart" className={classes.cart} onClick={() => addToCart(juice)}>Add to cart</Button>
            <Link to={"/juices"}>
                <Button className={classes.back}>Go Back</Button>
            </Link>
            </Paper>
        </div>
    )
}
export default SingleJuiceBody;
