import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import greenBackground from '../images/vitalii-pavlyshynets-kcRFW-Hje8Y-unsplash.jpg'
import Grid from "@material-ui/core/Grid";
import color from '../images/colorJuice.png'

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
        margin: '0'
    },
    box: {
        textAlign: "center",
        marginTop: "-3.7em",
        fontFamily: "BebasNeue, serif",
        height: "95vh",
        display: 'inline-block',
        paddingTop: '10em'
    },
    img: {
        display: 'inline-block',
        marginTop: "10em",
        marginRight: "15em",
    },
    welcomeText: {
        color: "black",
        alignItems: "center",
        [theme.breakpoints.down('sm')]: {
            fontSize: '3rem',
            fontWeight: 'bold'
        },
        [theme.breakpoints.up('md')]: {
            fontSize: '20rem',
        },
    },
    header: {
        color: "black",
        alignItems: "center",
        marginTop: "0.3em",
        [theme.breakpoints.down('sm')]: {
            fontSize: '2rem',
            fontWeight: 'bold'
        },
        [theme.breakpoints.up('md')]: {
            fontSize: '10rem',
        },
    },
    waves: {
        marginTop: '-31em',
        marginBottom: "0"
    }
}));

const HomeBody = () => {
    const classes = useStyles();
    return(
        <div className={classes.root}>
            <Grid container direction='row' justify='center'>
                <Grid item xs className={classes.box}>
                    <h2 className={classes.header}>Welcome to</h2>
                    <h1 className={classes.welcomeText}>Juicebox</h1>
                </Grid>
                <div className={classes.img}>
                    <img src={color}/>
                </div>
            </Grid>
            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1440 320" className={classes.waves}>
                <path fill="#fb8371" fill-opacity="1" d="M0,0L20,32C40,64,80,128,120,181.3C160,235,200,277,240,250.7C280,224,320,128,360,90.7C400,53,440,75,480,112C520,149,560,203,600,234.7C640,267,680,277,720,256C760,235,800,181,840,144C880,107,920,85,960,96C1000,107,1040,149,1080,138.7C1120,128,1160,64,1200,64C1240,64,1280,128,1320,149.3C1360,171,1400,149,1420,138.7L1440,128L1440,320L1420,320C1400,320,1360,320,1320,320C1280,320,1240,320,1200,320C1160,320,1120,320,1080,320C1040,320,1000,320,960,320C920,320,880,320,840,320C800,320,760,320,720,320C680,320,640,320,600,320C560,320,520,320,480,320C440,320,400,320,360,320C320,320,280,320,240,320C200,320,160,320,120,320C80,320,40,320,20,320L0,320Z"></path>
            </svg>
        </div>
    )
}
export default HomeBody
