import React, { useEffect, useState } from 'react';
import {Link, navigate} from '@reach/router';
import axios from 'axios';

const ShowOne = (props) => {
    const [item, setItem] = useState({})
    useEffect(() => {
        axios.get("http://localhost:8000/api/items/" + props.id)
            .then((res) => {
                console.log(res.data);
                setItem(res.data);
            })
            .catch((err) => console.log(err))
    }, [])
    const deleteItem = () => {
        axios.delete("http://localhost:8000/api/items/" + props.id)
            .then((data) => navigate("/items"))
            .catch((err) => console.log(err))
    }
    return (
        <div className="container">
            <p className="text-center h1"><span className="text-muted">Title:</span> {item.title}</p>
            <p className="text-center h1"><span className="text-muted">Price:</span> {item.price}</p>
            <p className="text-center h1"><span className="text-muted">Description:</span> {item.description}</p>
            <div className='text-center'>
                <button className="btn btn-primary mr-2" onClick={deleteItem} >Delete</button>
                <button className="btn btn-success mr-2" onClick={() => navigate("/items/" + props.id + "/edit")}>Edit</button>
                <Link to="/items">
                    <button className="btn btn-danger mr-2">Go Back</button>
                </Link>
            </div>
        </div>
    )
}
export default ShowOne;
