import React, {useReducer, useEffect, useState} from 'react';
import axios from "axios";

const Cart = ({ cart, setCart, juices, setJuices }) => {

    const [total, setTotal] = useState()

    const removeFromCart = (juice) => {
        let hardCopy = [...cart];
        hardCopy = hardCopy.filter((cartItem) => cartItem._id !== juice._id);
        setCart(hardCopy);
    };
    const getTotal = () => {
        setCart(current => [...current, juice.title]);
        setTotal(current => current + juice.price);
    }
    const listCart = cart.map((juice) => (
        <div>
            <h1>{juice.title}</h1>
            <h1>{juice.price}</h1>
            <input type="submit" value="remove" onClick={() => removeFromCart(juice)} />
        </div>
    ));

    return (
        <div>
            <h1>Current Message</h1>
            <div>{listCart}</div>
        </div>
    );
};

export default Cart;
