import React, {useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Box from "@material-ui/core/Box";
import greenBackground from '../images/vitalii-pavlyshynets-kcRFW-Hje8Y-unsplash.jpg'
import Grid from "@material-ui/core/Grid";

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: 'black',
        vh: '100%'
    },
    box: {
      backgroundImage: `url(${greenBackground})`,
        backgroundSize: 'cover',
        backgroundRepeat: "no-repeat",
        backgroundAttachment: "fixed",
        backgroundPosition: "center center",
        [theme.breakpoints.down('sm')]: {
            padding: theme.spacing(60),
        },
        [theme.breakpoints.up('md')]: {
            padding: theme.spacing(34)
        },
    },
    welcomeText: {
        color: "white",
        [theme.breakpoints.down('sm')]: {
            fontSize: '3rem',
        },
        [theme.breakpoints.up('md')]: {
            fontSize: '15rem',
        },
}}));

const HomeBody = () => {
    const classes = useStyles();
    return(
        <div className={classes.root}>
            <Grid container direction='row' justify='center'>
                <Grid item xs>
                    <div className={classes.box}>
                        <h2 className={classes.welcomeText}>Welcome to</h2>
                        <h1 className={classes.welcomeText}>JuiceCentral</h1>
                    </div>
                </Grid>
            </Grid>
        </div>
    )
}
export default HomeBody
