import React, {useEffect, useState} from 'react';
import {Link} from "@reach/router";
import axios from "axios";
import { makeStyles } from '@material-ui/core/styles';
import citrus from '../images/dhaya-eddine-bentaleb-vwnr7LQajac-unsplash.jpg'
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles({
    root: {
        maxWidth: 345,
    },
});

const JuiceBody = () => {
    const classes = useStyles();
    const [juices, setJuices] = useState([]);

    useEffect(() => {
        axios.get("http://localhost:8000/api/juices")
            .then((res) => setJuices(res.data))
            .catch((err) => console.log(err));
    }, [])

    return (
        <div className="container pt-5">
            {juices.map((juice) => (
                <Link to={"/juices/" + juice._id}>
                    <Card className={classes.root}>
                        <CardActionArea>
                            <CardMedia
                                component="img"
                                alt="Celery Apple"
                                height="140"
                                image="../images/dhaya-eddine-bentaleb-vwnr7LQajac-unsplash.jpg"
                                title="Contemplative Reptile"
                            />
                            <h1 className="text-center">{juice.title}</h1>
                        </CardActionArea>
                    </Card>
                </Link>
            ))}
        </div>
    )
}
export default JuiceBody
