import React, { useState } from 'react';
import ShoppingCartRoundedIcon from '@material-ui/icons/ShoppingCartRounded';
import RemoveShoppingCartRoundedIcon from '@material-ui/icons/RemoveShoppingCartRounded'
import {Link} from "@reach/router";
import axios from "axios";
import {makeStyles} from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
    },
    btn: {
        backgroundColor: 'white',
        color: '#DD415B',
        border: '3px solid red',
        padding: '0 30px',
        height: 48,
        borderRadius: 30,
        boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
    },
    create: {
        background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
        borderRadius: 30,
        border: 0,
        color: 'white',
        height: 48,
        padding: '0 30px',
        boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
    },
    title: {
        fontFamily: 'BebasNeue, serif',
        fontSize: '5em'
    },
    row: {
        display: 'flex',
        flexDirection: 'column',
        width: 350,
        margin: '5em'
    },
    added: {
        display: 'flex',
        flexDirection: 'column',
        width: 350,
        fontSize: '5em',
        color: 'black',
        fontFamily: 'BebasNeue, serif',
    },
    clicked: {
        display: 'flex',
        justifyContent: 'flex-end'
    }
}))

const Create = ({cart, setCart, created, setCreated}) => {
    const classes = useStyles();
    const [ingredients, setIngredients] = useState(false)
    const [redApples, setRedApples] = useState(false)
    const [greenApples, setGreenApples] = useState(false)
    const [kale, setKale] = useState(false)
    const [celery, setCelery] = useState(false)
    const [lemon, setLemon] = useState(false)
    const [beets, setBeets] = useState(false)
    const [tomatoes, setTomatoes] = useState(false)
    const [carrots, setCarrots] = useState(false)
    const [mintLeaves, setMintLeaves] = useState(false)

    const createJuice = (e) => {
        e.preventDefault();
        setCreated(true)
        console.log(created)
    }
    const addRedApple = (e) => {
        e.preventDefault();
        setRedApples(true)
    }
    const subtractRedApple = (e) => {
        e.preventDefault();
        setRedApples(false)
    }
    const addGreenApple = (e) => {
        e.preventDefault();
        setGreenApples(!greenApples)
    }
    const addKale = (e) => {
        e.preventDefault();
        setKale(!kale)
    }
    const addCelery = (e) => {
        e.preventDefault();
        setCelery(!celery)
    }
    const addLemon = (e) => {
        e.preventDefault();
        setLemon(!lemon)
    }
    const addBeets = (e) => {
        e.preventDefault();
        setBeets(!beets)
    }
    const addTomatoes = (e) => {
        e.preventDefault();
        setTomatoes(!tomatoes)
    }
    const addCarrots = (e) => {
        e.preventDefault();
        setCarrots(!carrots)
    }
    const addMintLeaves = (e) => {
        e.preventDefault();
        setMintLeaves(!mintLeaves)
    }

    return(
        <div className={classes.root}>
            <form onSubmit={createJuice}>
                <div>
                    <div className={classes.row}>
                        <h1 className={classes.title}>Red Apples</h1>
