    const [newItem, setNewItem] = useState(true)

    const onClick = (e) => {
        setNewItem(!juices.favorite);
        console.log(setNewItem)
    };

    useEffect(() => {
        axios.get("http://localhost:8000/api/juices")
            .then((res) => setJuices(res.data))
            .catch((err) => console.log(err));
    }, [])

    return (
        <div className="container pt-5">
            {juices.map((juice) => (
                <div>
                    <Link to={"/juices/" + juice._id}>
                        <h1 className="text-center" >{juice.title}</h1>
                    </Link>
                    <input type="submit" onClick={() => addToCart(juice)} />
                </div>
            ))}
        </div>
    )
}
export default ShowAll
