import React, { useState } from 'react';
import Homepage from "./pages/Homepage";
import './App.css';
import JuiceBody from './components/JuiceBody'
import SingleJuiceBody from "./components/SingleJuiceBody"
import { Router } from "@reach/router";

function App() {
    const [cart, setCart] = useState([]);

    const thingsInCart = ( testCart ) => {
        setCart( testCart )
    }

  return (
    <div className="App">
        <Router>
          <Homepage default/>
          <JuiceBody onNewMessage={ thingsInCart} path="/juices" />
          <SingleJuiceBody path="/juices/:id" />
        </Router>
    </div>
  );
}

export default App;
