import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import greenBackground from '../images/vitalii-pavlyshynets-kcRFW-Hje8Y-unsplash.jpg'
import Grid from "@material-ui/core/Grid";
import color from '../images/colorJuice.png'

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    box: {
        textAlign: "center",
        marginTop: "-3.7em",
        fontFamily: "BebasNeue, serif",
        height: "100vh",
        display: 'inline-block',
        paddingTop: '10em'
    },
    img: {
        display: 'inline-block',
        marginTop: "10em",
        marginRight: "15em",
    },
    welcomeText: {
        color: "black",
        alignItems: "center",
        [theme.breakpoints.down('sm')]: {
            fontSize: '3rem',
            fontWeight: 'bold'
        },
        [theme.breakpoints.up('md')]: {
            fontSize: '20rem',
        },
    },
    header: {
        color: "black",
        alignItems: "center",
        marginTop: "0.3em",
        [theme.breakpoints.down('sm')]: {
            fontSize: '2rem',
            fontWeight: 'bold'
        },
        [theme.breakpoints.up('md')]: {
            fontSize: '10rem',
        },
    },
    waves: {
        marginTop: '-33em'
    }
}));

const HomeBody = () => {
    const classes = useStyles();
    return(
        <div className={classes.root}>
            <Grid container direction='row' justify='center'>
                <Grid item xs className={classes.box}>
                    <h2 className={classes.header}>Welcome to</h2>
                    <h1 className={classes.welcomeText}>Juicebox</h1>
                </Grid>
                <div className={classes.img}>
                    <img src={color}/>
                </div>
            </Grid>
            <svg className={classes.waves} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1440 320"><path fill="#f0783e" fill-opacity="1" d="M0,160L20,154.7C40,149,80,139,120,128C160,117,200,107,240,96C280,85,320,75,360,106.7C400,139,440,213,480,245.3C520,277,560,267,600,224C640,181,680,107,720,106.7C760,107,800,181,840,202.7C880,224,920,192,960,154.7C1000,117,1040,75,1080,74.7C1120,75,1160,117,1200,154.7C1240,192,1280,224,1320,213.3C1360,203,1400,149,1420,122.7L1440,96L1440,320L1420,320C1400,320,1360,320,1320,320C1280,320,1240,320,1200,320C1160,320,1120,320,1080,320C1040,320,1000,320,960,320C920,320,880,320,840,320C800,320,760,320,720,320C680,320,640,320,600,320C560,320,520,320,480,320C440,320,400,320,360,320C320,320,280,320,240,320C200,320,160,320,120,320C80,320,40,320,20,320L0,320Z"></path></svg>
        </div>
    )
}
export default HomeBody
