import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import greenBackground from '../images/vitalii-pavlyshynets-kcRFW-Hje8Y-unsplash.jpg'
import Grid from "@material-ui/core/Grid";

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    box: {
        background: `linear-gradient(0deg, rgba(255,255,255,0) 65%, rgba(42,42,42,1) 100%), url(${greenBackground})`,
        backgroundSize: 'cover',
        backgroundRepeat: "no-repeat",
        backgroundAttachment: "fixed",
        backgroundPosition: "center center",
        textAlign: "center",
        verticalAlign: "center",
        marginTop: "-3.7em",
        [theme.breakpoints.down('sm')]: {
            padding: theme.spacing(10)
        },
        [theme.breakpoints.up('md')]: {
            padding: theme.spacing(25)
        },
        height: "100vh",
    },
    welcomeText: {
        color: "white",
        alignItems: "center",
        [theme.breakpoints.down('sm')]: {
            fontSize: '3rem',
            fontWeight: 'bold'
        },
        [theme.breakpoints.up('md')]: {
            fontSize: '15rem',
        },
}}));

const HomeBody = () => {
    const classes = useStyles();
    return(
        <div className={classes.root}>
            <Grid container direction='row' justify='center'>
                <Grid item xs >
                    <div className={classes.box}>
                        <h2 className={classes.welcomeText}>Welcome to</h2>
                        <h1 className={classes.welcomeText}>The Juicebox</h1>
                    </div>
                </Grid>
            </Grid>
        </div>
    )
}
export default HomeBody
