import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import greenBackground from '../images/vitalii-pavlyshynets-kcRFW-Hje8Y-unsplash.jpg'
import Grid from "@material-ui/core/Grid";
import color from '../images/colorJuice.png'

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
        margin: '0'
    },
    box: {
        textAlign: "center",
        marginTop: "-3.7em",
        fontFamily: "BebasNeue, serif",
        height: "95vh",
        display: 'inline-block',
        paddingTop: '10em'
    },
    img: {
        display: 'inline-block',
        marginTop: "10em",
        marginRight: "15em",
    },
    welcomeText: {
        color: "black",
        alignItems: "center",
        [theme.breakpoints.down('sm')]: {
            fontSize: '3rem',
            fontWeight: 'bold'
        },
        [theme.breakpoints.up('md')]: {
            fontSize: '20rem',
        },
    },
    header: {
        color: "black",
        alignItems: "center",
        marginTop: "0.3em",
        [theme.breakpoints.down('sm')]: {
            fontSize: '2rem',
            fontWeight: 'bold'
        },
        [theme.breakpoints.up('md')]: {
            fontSize: '10rem',
        },
    },
    waves: {
        marginTop: '-26em',
        marginBottom: "0"
    }
}));

const HomeBody = () => {
    const classes = useStyles();
    return(
        <div className={classes.root}>
            <Grid container direction='row' justify='center'>
                <Grid item xs className={classes.box}>
                    <h2 className={classes.header}>Welcome to</h2>
                    <h1 className={classes.welcomeText}>Juicebox</h1>
                </Grid>
                <div className={classes.img}>
                    <img src={color}/>
                </div>
            </Grid>
            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1440 320" className={classes.waves}>
                <path fill="#72CA76" fill-opacity="1" d="M0,128L24,112C48,96,96,64,144,64C192,64,240,96,288,128C336,160,384,192,432,202.7C480,213,528,203,576,186.7C624,171,672,149,720,128C768,107,816,85,864,85.3C912,85,960,107,1008,122.7C1056,139,1104,149,1152,138.7C1200,128,1248,96,1296,112C1344,128,1392,192,1416,224L1440,256L1440,320L1416,320C1392,320,1344,320,1296,320C1248,320,1200,320,1152,320C1104,320,1056,320,1008,320C960,320,912,320,864,320C816,320,768,320,720,320C672,320,624,320,576,320C528,320,480,320,432,320C384,320,336,320,288,320C240,320,192,320,144,320C96,320,48,320,24,320L0,320Z"></path>
            </svg>
        </div>
    )
}
export default HomeBody
