import React, {useEffect, useState} from 'react';
import {Link} from "@reach/router";
import { makeStyles } from '@material-ui/core/styles';
import axios from "axios";
import Button from "@material-ui/core/Button";
import clsx from 'clsx';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';

const useStyles = makeStyles((theme) => ({
    div: {
        display: 'inline-block',
        fontFamily: 'Pacifico, serif',
        color: 'black',
        marginRight: '5em',
        width: '90vw'
    },
    card: {
        width: 345,
        height: 500,
        fontFamily: 'Pacifico, serif',
        color: 'black',
        margin: '50'
    },
    media: {
        height: 0,
        paddingTop: '60%'
    },
    header: {
        fontFamily: 'Pacifico, serif',
        align: 'left',
    },
}));

const JuiceBody = ({ cart, setCart, juices, setJuices, total, setTotal, selected, setSelected}) => {

    const classes = useStyles();

    const addToCart = (juice) => {
        setCart([...cart, juice])
        setTotal((total + juice.price) * 1.0625)
    }
    const addId = (juice) => {
        setSelected(juice._id)
    }

    const listJuices = juices.map((juice) => (
        <div className={classes.div}>
            <Card className={classes.card}>
                <Link to={"/juices/" + juice._id} className={classes.header}>
                <CardHeader
                    title={juice.title}
                    subheader={juice.type}

                />
                <CardMedia
                    className={classes.media}
                    image={juice.url}
                />

                    <h1 className="text-center" onClick={() => addId(juice)}>{juice.title}</h1>
                </Link>
                <Button type="submit" onClick={() => addToCart(juice)}>Add to cart </Button>
            </Card>
        </div>
    ));

    return (
        <div className="container pt-5">
            <div> {listJuices} </div>
        </div>
    )
}
export default JuiceBody;
