import React, { useEffect, useState } from 'react';
import {Link, navigate} from '@reach/router';
import axios from 'axios';
import Button from "@material-ui/core/Button";
import { makeStyles } from '@material-ui/core/styles';

const SingleJuiceBody = ({ cart, setCart, total, setTotal, selected, setSelected, juice, setJuice}) => {

    const onClickHandler = (e) => {
        e.preventDefault();
    }

    useEffect(() => {
        axios.get("http://localhost:8000/api/juices/" + selected)
            .then((res) => {
                console.log(res.data);
                setJuice(res.data);
            })
            .catch((err) => console.log(err))
    }, [])

    const addToCart = (juice) => {
        setCart([...cart, juice])
        setTotal((total + juice.price) * 1.0625)
    }

    return (
        <div className="container">
            <p className="text-center h1"><span className="text-muted">Title:</span> {juice.title}</p>
            <p className="text-center h1"><span className="text-muted">Price:</span> {juice.price}</p>
            <p className="text-center h1"><span className="text-muted">Description:</span> {juice.description}</p>
            <p className="text-center h1"><span className="text-muted">Type:</span> {juice.type}</p>
            <Button type="submit" value="Add To Cart" onClick={() => addToCart(juice)}>Add to cart</Button>
            <Link to={"/juices"}>
                <Button>Go Back</Button>
            </Link>
        </div>
    )
}
export default SingleJuiceBody;
