import React, { useState, useEffect } from 'react';
import Homepage from "./pages/Homepage";
import './App.css';
import JuiceBody from './components/JuiceBody'
import SingleJuiceBody from "./components/SingleJuiceBody"
import { Router } from "@reach/router";
import axios from "axios";
import CartPage from "./pages/CartPage";

function App() {
    const [cart, setCart] = useState([]);
    const [juices, setJuices] = useState([])
    useEffect(() => {
        axios.get("http://localhost:8000/api/juices")
            .then((res) => setJuices(res.data))
            .catch((err) => console.log(err))
    }, [])

  return (
    <div className="App">
        <Router>
          <Homepage default/>
          <JuiceBody cart={cart} setCart={setCart} juices={juices} setJuices={setJuices} path="/juices" />
          <SingleJuiceBody path="/juices/:id" />
          <CartPage cart={cart} setCart={setCart} juices={juices} setJuices={setJuices} path="/cart" />
        </Router>
    </div>
  );
}

export default App;
