import React, { useEffect, useState } from 'react';
import {Link, navigate} from '@reach/router';
import axios from 'axios';

const ShowOne = (props) => {
    const [juice, setJuice] = useState({})
    useEffect(() => {
        axios.get("http://localhost:8000/api/juices/" + props.id)
            .then((res) => {
                console.log(res.data);
                setJuice(res.data);
            })
            .catch((err) => console.log(err))
    }, [])
    return (
        <div className="container">
            <p className="text-center h1"><span className="text-muted">Title:</span> {juice.title}</p>
            <p className="text-center h1"><span className="text-muted">Price:</span> {juice.price}</p>
            <p className="text-center h1"><span className="text-muted">Description:</span> {juice.description}</p>
            <div className='text-center'>
                <Link to="/juices">
                    <button className="btn btn-danger mr-2">Go Back</button>
                </Link>
            </div>
        </div>
    )
}
export default ShowOne;
