import React, {useReducer, useEffect, useState} from 'react';
import axios from "axios";
import { makeStyles } from '@material-ui/core/styles';

const Cart = ({ cart, setCart, juices, setJuices, total, setTotal }) => {

    const removeFromCart = (juice) => {
        let hardCopy = [...cart];
        hardCopy = hardCopy.filter((cartItem) => cartItem._id !== juice._id);
        setCart(hardCopy);
        let totalCopy = [...total];
        totalCopy = totalCopy.filter((totalItem) => totalItem._id !==juice._id)
        setTotal(totalCopy)
    };

    const listCart = cart.map((juice) => (
        <div>
            <h1>{juice.title}</h1>
            <h1>{juice.price}</h1>
            <input type="submit" value="Remove" onClick={() => removeFromCart(juice)} />
        </div>
    ));

    return (
        <div>
            <h1>Cart</h1>
            <h2>Cart Items: {cart.length}</h2>
            <div>{listCart}</div>
            <h2>Total: {total.toFixed(2)}</h2>
        </div>
    );
};

export default Cart;
