import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Button from "@material-ui/core/Button";
import { Link, navigate } from "@reach/router";
import AppBar from "@material-ui/core/AppBar";
import Typography from "@material-ui/core/Typography";
import Toolbar from "@material-ui/core/Toolbar";

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    title: {
        flexGrow: 1,
    },
    menuButton: {
      marginRight: theme.spacing(2)
    },
    btn: {
        background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
        borderRadius: 30,
        border: 0,
        color: 'white',
        height: 48,
        marginTop: '.5em',
        padding: '0 30px',
        boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
    },
    logo: {
        fontFamily: 'Pacifico, serif',
        align: 'left',
    },
}));

const Navbar = ({ cart, setCart }) => {
    const classes = useStyles();

    return (
        <div className={classes.root}>
            <AppBar position='static'>
                <Toolbar>
                    <Typography variant="h6" className={classes.title}>
                        <div className={classes.menuButton}>
                        News
                        </div>
                    </Typography>
                    <Button color="inherit" onClick={() => navigate("/juice")}>Home</Button>
                    <Button color="inherit" onClick={() => navigate("/juices")}>Juices</Button>
                    <Button color="inherit" onClick={() => navigate("/create")}>Create</Button>
                    <Button color="inherit" nClick={() => navigate("/cart")}>Cart</Button>
                </Toolbar>
            </AppBar>
        </div>
    )
}
export default Navbar


