import React, {useEffect, useState} from 'react';
import {Link} from "@reach/router";
import axios from "axios";

const JuiceBody = ({ cart, setCart}) => {
    const [juices, setJuices] = useState([]);

    const addToCart = (juice) => {
        setCart([...cart, juice])
    }

    const listJuices = juices.map((juice) => (
        <div>
            <Link to={"/juices/" + juice._id}>
                <h1 className="text-center" >{juice.title}</h1>
            </Link>
            <input type="submit" onClick={() => addToCart(juice)} />
        </div>
    ));
    const removeFromCart = (juice) => {
        let hardCopy = [...cart];
        hardCopy = hardCopy.filter((cartItem) => cartItem.id !== juice.id);
        setCart(hardCopy);
    };
    const listCart = cart.map((juice) => (
        <div>
            <h1>{juice.title}</h1>
            <input type="submit" value="remove" onClick={() => removeFromCart(juice)} />
        </div>
    ))

    useEffect(() => {
        axios.get("http://localhost:8000/api/juices")
            .then((res) => setJuices(res.data))
            .catch((err) => console.log(err))
    }, [])

    return (
        <div className="container pt-5">
            <div> {listJuices} </div>
            <h1>Cart</h1>
            <div>{listCart}</div>
        </div>
    )
}
export default JuiceBody;
