import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Button from "@material-ui/core/Button";
import { Link, navigate } from "@reach/router";

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
        background: 'rgba(0, 0, 0, 0)',
        fontColor: 'white',
    },
    button: {
        textAlign: 'center',
        [theme.breakpoints.down('xs')]: {
            padding: theme.spacing(1)
        },
        [theme.breakpoints.up('sm')]: {
            padding: theme.spacing(2),
            fontSize: '1em'
        },
    }

}));

const Navbar = ({ cart, setCart }) => {
    const classes = useStyles();

    return (
        <div className={classes.root}>
            <Grid container direction='row' justify='center'>
                <Grid item xs={3} sm={1}>
                    <Button className={classes.button} onClick={() => navigate("/juice")}>Home</Button>
                </Grid>
                <Grid item xs={3} sm={1}>
                    <Button className={classes.button} onClick={() => navigate("/juices")}>Juices</Button>
                </Grid>
                <Grid item xs={3} sm={1}>
                    <Button className={classes.button} onClick={() => navigate("/create")}>Create</Button>
                </Grid>
                <Grid item xs={3} sm={1}>
                    <Button className={classes.button} onClick={() => navigate("/cart")}>Cart ({cart.length})</Button>
                </Grid>
            </Grid>
        </div>
    )
}
export default Navbar
