import React, {useEffect, useState} from 'react';
import {Link} from "@reach/router";
import axios from "axios";

const JuiceBody = () => {
    const [juices, setJuices] = useState([]);
    const [cart, setCart] = useState([])

    const addToCart = (juice) => {
        setCart([...cart, juice])
    }

    const listJuices = juices.map((juice) => (
        <div>
            <Link to={"/juices/" + juice._id}>
                <h1 className="text-center" >{juice.title}</h1>
            </Link>
            <input type="submit" onClick={() => addToCart(juice)}>Add to Cart</input>
        </div>
    ))

    useEffect(() => {
        axios.get("http://localhost:8000/api/juices")
            .then((res) => setJuices(res.data))
            .catch((err) => console.log(err))
    }, [])

    return (
        <div className="container pt-5">
            <div> {listJuices} </div>
        </div>
    )
}
export default JuiceBody;
