import React, { useState, useEffect } from 'react';
import Homepage from "./pages/Homepage";
import './App.css';
import JuiceBody from './components/JuiceBody';
import SingleJuiceBody from "./components/SingleJuiceBody";
import Juicespage from "./pages/Juicespage";
import { Router } from "@reach/router";
import axios from "axios";
import CartPage from "./pages/CartPage";
import SingleJuicePage from "./pages/SingleJuicePage";
import CreatePage from "./pages/CreatePage";

function App() {
    const [cart, setCart] = useState([]);
    const [juices, setJuices] = useState([]);
    const [juice, setJuice] = useState("");
    const [total, setTotal] = useState(0)
    useEffect(() => {
        axios.get("http://localhost:8000/api/juices")
            .then((res) => setJuices(res.data))
            .catch((err) => console.log(err))
    }, [])

  return (
    <div className="App">
        <Router>
          <Homepage className="home" default/>
          <Juicespage cart={cart} setCart={setCart} juices={juices} setJuices={setJuices} juice={juice} setJuice={setJuice} total={total} setTotal={setTotal} path="/juices" />
          <SingleJuicePage cart={cart} setCart={setCart} juices={juices} setJuices={setJuices} juice={juice} setJuice={setJuice} path="/juices/:id" />
          <CartPage cart={cart} setCart={setCart} juices={juices} setJuices={setJuices} total={total} setTotal={setTotal} path="/cart" />
          <CreatePage cart={cart} setCart={setCart} path="/create" />
        </Router>
    </div>
  );
}

export default App;
