import {Link} from "@reach/router";
import axios from "axios";

const Create = ({cart, setCart}) => {
    let [apples, setApples] = useState(0)
    let apple = 0
    const appleHandler = () => {
        apple += 1
    }

    return(
        <div>
            <h1>Create Your Own Juice</h1>

                <h3>Ingredients</h3>
                <button>-</button><h4>Apples {apple}</h4><button onClick={appleHandler}>+</button>


        </div>
    )
}
export default Create
