import React, {useEffect, useState} from 'react';
import axios from "axios";



const Cart = (cart, setCart) => {
    const removeFromCart = (juice) => {
        let hardCopy = [...cart];
        hardCopy = hardCopy.filter((cartItem) => cartItem.id !== juice.id);
        setCart(hardCopy);
    };
    const listCart = cart.map((juice) => (
        <div>
            <h1>{juice.title}</h1>
            <input type="submit" value="remove" onClick={() => removeFromCart(juice)} />
        </div>
    ));
    useEffect(() => {
        axios.get("http://localhost:8000/api/juices")
            .then((res) => setJuices(res.data))
            .catch((err) => console.log(err))
    }, [])

    return (
        <>
            <h1>Current Message</h1>
            <div>{listCart}</div>
        </>
    );
};

export default Cart;
