import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import greenBackground from '../images/vitalii-pavlyshynets-kcRFW-Hje8Y-unsplash.jpg'
import Grid from "@material-ui/core/Grid";
import color from '../images/colorJuice.png'

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    box: {
        textAlign: "center",
        verticalAlign: "center",
        marginTop: "-3.7em",
        fontFamily: "BebasNeue, serif",
        [theme.breakpoints.down('sm')]: {
            padding: theme.spacing(10)
        },
        [theme.breakpoints.up('md')]: {
            padding: theme.spacing(25)
        },
        height: "100vh",
    },
    img: {
      display: 'inline-block',
        marginTop: '-1000'
    },
    welcomeText: {
        color: "black",
        alignItems: "center",
            [theme.breakpoints.down('sm')]: {
            fontSize: '3rem',
            fontWeight: 'bold'
        },
        [theme.breakpoints.up('md')]: {
            fontSize: '20rem',
        },
    },
    header: {
        color: "black",
        alignItems: "center",
        marginTop: "0.3em",
        [theme.breakpoints.down('sm')]: {
            fontSize: '2rem',
            fontWeight: 'bold'
        },
        [theme.breakpoints.up('md')]: {
            fontSize: '10rem',
        },
    },
    text: {
        display: 'inline-block'
    }
}));

const HomeBody = () => {
    const classes = useStyles();
    return(
        <div className={classes.root}>
            <Grid container direction='row' justify='center'>
                <Grid item xs className={classes.box}>
                    <img src={color} className={classes.img}/>
                    <div className={classes.text}>
                        <h2 className={classes.header}>Welcome to</h2>
                        <h1 className={classes.welcomeText}>Juicebox</h1>
                    </div>
                </Grid>
            </Grid>
        </div>
    )
}
export default HomeBody
