        setNewColor("");
    };

    useEffect(() => {
        axios.get("http://localhost:8000/api/juices")
            .then((res) => setJuices(res.data))
            .catch((err) => console.log(err));
    }, [])

    return (
        <div className="container pt-5">
            {juices.map((juice) => (
                <Link to={"/juices/" + juice._id}>
                    <h1 className="text-center">{juice.title}</h1>
                </Link>
            ))}
        </div>
    )
}
