import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Button from "@material-ui/core/Button";
import { Link, navigate } from "@reach/router";

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
        background: 'rgba(0, 0, 0, 0)',
    },
    btn: {
        background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
        borderRadius: 3,
        border: 0,
        color: 'white',
        height: 48,
        padding: '0 30px',
        boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
    },
    label: {
        textTransform: 'capitalize',
    },
    logo: {
        fontFamily: 'Pacifico, serif',
        align: 'left',
    },
}));

const Navbar = ({ cart, setCart }) => {
    const classes = useStyles();

    return (
        <div className={classes.root}>
            <Grid container direction='row' justify='center'>
                    <Button className={{root: classes.btn, label: classes.label }} onClick={() => navigate("/juice")}>Home</Button>
                <Grid item xs={3} sm={1}>
                    <Button className={classes.button} onClick={() => navigate("/juices")}>Juices</Button>
                </Grid>
                <Grid item xs={3} sm={1}>
                    <Button className={classes.button} onClick={() => navigate("/create")}>Create</Button>
                </Grid>
                <Grid item xs={3} sm={1}>
                    <Button className={classes.button} onClick={() => navigate("/cart")}>Cart ({cart.length})</Button>
                </Grid>
            </Grid>
        </div>
    )
}
export default Navbar
